import { useState, useMemo } from 'react';
import { AlertCircle, ChevronDown, ChevronUp, Phone, Calendar } from 'lucide-react';
import { useTranslation } from 'react-i18next';
import { SymptomLog, FirestoreDate } from '../../types';
import { toJsDate } from '../../utils/dateUtils';
import { TFunction } from 'i18next';

interface TrendAnalysis {
    trend: 'none' | 'warning' | 'increasing' | 'decreasing' | 'stable';
    message: string | null;
    cta: {
        icon: React.ElementType;
        text: string;
        action: string;
    } | null;
}

// Check if a date is today
const isToday = (timestamp: FirestoreDate | undefined) => {
    const date = toJsDate(timestamp);
    if (!date) return false;
    const today = new Date();
    return date.toDateString() === today.toDateString();
};

// Check if date is within last N days
const isWithinDays = (timestamp: FirestoreDate | undefined, days: number) => {
    const date = toJsDate(timestamp);
    if (!date) return false;
    const cutoff = new Date();
    cutoff.setDate(cutoff.getDate() - days);
    return date >= cutoff;
};

// Get trend analysis for symptoms
const analyzeTrend = (symptoms: SymptomLog[], t: TFunction): TrendAnalysis => {
    if (symptoms.length === 0) return { trend: 'none', message: null, cta: null };

    // Count symptoms by day for last 7 days
    const last3Days = symptoms.filter(s => isWithinDays(s.loggedAt, 3)).length;
    const prev4Days = symptoms.filter(s =>
        isWithinDays(s.loggedAt, 7) && !isWithinDays(s.loggedAt, 3)
    ).length;

    // Count severe symptoms
    const severeCount = symptoms.filter(s =>
        s.bodyLocation?.severity?.id === 'severe'
    ).length;

    // Analyze patterns
    if (severeCount >= 2) {
        return {
            trend: 'warning',
            message: t('severe_symptoms_count', { count: severeCount }),
            cta: { icon: Phone, text: t('contact_doctor_cta'), action: 'call' }
        };
    }

    if (last3Days > prev4Days * 1.5 && last3Days >= 3) {
        return {
            trend: 'increasing',
            message: t('increasing_symptoms_msg'),
            cta: { icon: Calendar, text: t('book_doctor_cta'), action: 'book' }
        };
    }

    if (last3Days < prev4Days * 0.5) {
        return {
            trend: 'decreasing',
            message: t('decreasing_symptoms_msg'),
            cta: null
        };
    }

    return {
        trend: 'stable',
        message: t('stable_symptoms_msg', { count: symptoms.length }),
        cta: null
    };
};

interface SymptomSummaryProps {
    symptomLogs?: SymptomLog[];
    onViewReport?: () => void;
    hideTitle?: boolean;
}

// Helper to get friendly advice based on symptom
const getSymptomAdvice = (symptomLabel: string, t: TFunction): string => {
    const label = symptomLabel.toLowerCase();
    if (label.includes('kvalme')) return t('symptom_advice_nausea', 'Kvalme kan lindres med ingefÃ¦r-te og frisk luft.');
    if (label.includes('hovedpine')) return t('symptom_advice_headache', 'Husk at drikke vand og sikre ro omkring dig.');
    if (label.includes('svimmel')) return t('symptom_advice_dizzyness', 'Rejs dig langsomt og stÃ¸t dig til noget fast.');
    if (label.includes('smerte')) return t('symptom_advice_pain', 'SpÃ¸rg om medicin skal justeres, hvis smerter fortsÃ¦tter.');
    return t('symptom_advice_generic', 'Ring og hÃ¸r hvordan det gÃ¥r. En stemme varmer.');
};

