        return { todaySymptoms: today, weekSymptoms: week };
    }, [symptomLogs]);

    // Get trend analysis
    const weeklySymptoms = symptomLogs.filter(s => isWithinDays(s.loggedAt, 7));
    const trend = useMemo(() => analyzeTrend(weeklySymptoms, t), [weeklySymptoms, t]);

