// Get week number of the year
export const getWeekNumber = () => {
    const now = new Date();
    const start = new Date(now.getFullYear(), 0, 1);
    const diff = now.getTime() - start.getTime();
    const oneWeek = 604800000; // ms in a week
    return Math.floor(diff / oneWeek);
};

